    if (this.getNextX() > 0) this.setNextDirection( Direction.RIGHT);
    if (this.getNextY() < 0) this.setNextDirection( Direction.DOWN);
    if (this.getNextY() > 0) this.setNextDirection( Direction.UP);
    
    if (this.getNextX() !== 0 || this.getNextY() !== 0)
        return true;
    else
        return false;
